$("#hide").click(function(){
    $("div").hide(5000);
})

$("#show").click(function(){
    $("div").show(1000);
});

$("#toggle").click(function(){
    $("div").toggle(1000);
});

$("#fadeOut").click(function(){
    $("img").fadeOut(1000);
});

$("#fadeIn").click(function(){
    $("img").fadeIn(1000);
});

$("#fadeToggle").click(function(){
    $("img").fadeToggle(1000);
});


$("#slideUp").click(function(){
    $("div").slideUp(1000);
});

$("#slideDown").click(function(){
    $("div").slideDown(1000);
});

$("#slideToggle").click(function(){
    $("div").slideToggle(1000);
});